import React, {Component} from 'react';
import '../css/Nav.css';
import menu from '../assets/menu.svg'


class Nav extends Component {
    componentDidMount() {
        const mobileMenu = document.querySelector(".mobile-menu");
        const nav = document.querySelector(".nav-header");
        const navLinks = document.querySelectorAll(".nav-links li");

        const toggleNav = () => {
          nav.classList.toggle("nav-active");
          console.log('test')
          navLinks.forEach((link, index) => {
            if (link.style.animation) {
              link.style.animation = "";
            } else {
              link.style.animation = `navLinkFade 0.5s ease forwards ${index / 7}s`;
            }
          });
        };

        mobileMenu.addEventListener("click", toggleNav);

        this.toggleNav = toggleNav;

      }

      componentWillUnmount() {
        const mobileMenu = document.querySelector(".mobile-menu");
        mobileMenu.removeEventListener("click", this.toggleNav);
      }

    render(){
      return (
        <div className="Nav">
                <img id="mobile-cta" className="mobile-menu" src={menu} alt="Open Navigation"/>

              <nav className="nav-header" id="nav-header">
        <ul className="nav-links nav-active">
            <li>
                <a href="#about">About</a>
            </li>
            <li>
                <a href="#work">Work</a>
            </li>
            <li>
                <a href="medina.ahmed.mo@gmail.com">Contact</a>
            </li>
            <li>
                <a href="resume.pdf">Résumé</a>
            </li>

        </ul>

    </nav>
        </div>
      );
    }
  }

  export default Nav;
